class Foo {
  string foo(integer n) {
    if (n == 1) {
      return "one";
    }

    return "not one";
  }
}


class Foo {
  string foo(integer n) {
    if (n > 0) {           // +1
      if (n == 1) {        // +2
        return "one";
      }

      return "positive";
    }

    return "negative or 0";
  }
}


class Foo {
  string foo(integer n) {
    if (n > 0) {           // +1
      return "positive";
    }

    if (n == 0) {          // +1
      return "zero";
    }

    if (n < 0) {           // +1
      return "negative";
    }
  }
}


class Foo {
  string foo(integer n) {
    if (n > 0) {           // +1
      return "positive";
    } else if (n < 0) {    // +1
      return "negative";
    } else {               // +1
      return "zero";
    }
  }
}


class Foo {
  string foo() {
    for (integer i = 0; i < 10; i++) { // +1
      if (i == 3) {                    // +2
        return "three";
      }
    }

    return "done";
  }
}


class Foo {
  string foo() {
    Integer[] myInts = new Integer[] {1, 2, 3};
    for (Integer i : myInts) {         // +1
      if (i == 3) {                    // +2
        return "three";
      }
    }

    return "done";
  }
}


class Foo {
  string foo() {
    Integer[] myInts = new Integer[] {1, 2, 3};
    for (Integer i : myInts) {         // +1
      if (i == 3) {                    // +2
        continue;                      // +1
      }
    }

    return "done";
  }
}


class Foo {
  string foo() {
    Integer[] myInts = new Integer[] {1, 2, 3};
    for (Integer i : myInts) {         // +1
      if (i == 3) {                    // +2
        break;                      // +1
      }
    }

    return "done";
  }
}
