test-code.code:
@isTest
public class Foo {
     @isTest
    static void methodATest() {
        System.assertNotEquals('123', o.StageName); // not good
        System.assertEquals('123', o.StageName, o); // good
        System.assert(1==1); // not good
        System.assert(1==1, 2); // good
    }
}

@isTest(seeAllData = true)
public class Foo {
     @isTest
    static void methodATest() {
        System.assertEquals('123', o.StageName, o); // good
        System.assert(1==1, 2); // good
    }
}
