    public String doSomeThing(String uri, String cachedPath, int times) {
        var prior = System.nanoTime(); // side effect here

        // isn't a side-effect as those look like a getter
        if (System.getProperties().isEmpty()) {
            return "";
        }

        return uri + prior;
    }
}

